mapper.insert:
insert into m_language (
    id       --識別子
  , name     --言語名
  , country  --国名

  , remarks  --説明

  , createUserId  --作成者ID
  , versionNo     --バージョンNo
) values (

    coalesce((select max(id) from m_language), 0) + 1


    #{id}

  , #{name}
  , #{country}

  , #{remarks}

  , #{createUserId}
  , 1
)
