mapper.insert:
insert into m_project (
    id       --識別子
  , name     --プロジェクト名

  , description  --プロジェクトの説明

  , manager_id  --プロジェクトの管理者

  , opened      --公開

  , createUserId  --作成者ID
  , versionNo     --バージョンNo
) values (

    coalesce((select max(id) from m_project), 0) + 1


    #{id}

  , #{name}

  , #{description}

  , #{manager_id}

  , #{opened}

  , #{createUserId}
  , 1
)
